import logo1 from "../imgs/Gujarat_Police_Logo.png";

//import { useNavigate } from "react-router-dom"; 

import "./Otp.css";
import React from "react";




const Otp = () => {

    const handleChange = (e) => {
        const { name, value } = e.target
        this.setState({
          [name]: value
        })
      }
   const onSubmitOTP = (e) => {
        e.preventDefault()
        const code = this.state.otp
        console.log(code)
        window.confirmationResult.confirm(code).then((result) => {
          // User signed in successfully.
          const user = result.user;
          console.log(JSON.stringify(user))
          alert("User is verified")
          window.location = '/feedbackcitizen';
          // ...
        }).catch((error) => {
          // User couldn't sign in (bad verification code?)
          // ...
        });
      }


  return (
    <div className="secmain">
        <div className="firstformain">
          <div className="thirdformain">

            <form onSubmit={this.onSignInSubmit.bind(this)}>
            <h3><img src={logo1} alt="" /></h3>
            <h2>Welcome To Our Portal</h2>
              <p className="thank">Thanks for giving feedback<br />પ્રતિસાદ આપવા બદલ આભાર</p>
              <fieldset id="dff">
                <label htmlFor="name">Mobile Number<br />મોબાઇલ નંબર:</label>
                <input class="inputformain" type="number" name="mobile" required onChange={this.handleChange} />
                <button type="submit" >Submit</button>
              </fieldset>
            </form>


            <form onSubmit={this.onSubmitOTP}>
            <fieldset id="dff">
            <label htmlFor="name">Enter OTP<br />OTP દાખલ કરો</label>
            {/* <h6 >OTP Sent to/ओटीपी भेजा गया <span id="mobile"></span><br /></h6> */}
              <input class="inputformain" type="text" name="otp"required onChange={this.handleChange} />
              <button type="submit">Submit OTP</button>
         </fieldset>

            </form>

          </div>
        </div>
      </div>
  )
}

export default Otp
